import React from 'react';


import { CATEGORIES, PRODUCTS } from '../data/dummy-data';


import {View} from 'react-native';
import SearchableFlatlist from '../components/SearchableFlatlist';

const CategoryProductsScreen = props => {



  const catId = props.navigation.getParam('categoryId');


  const displayedProducts = PRODUCTS.filter(
    products => products.categoryId.indexOf(catId) >= 0
  );


  return (


   <SearchableFlatlist listData={displayedProducts}  navigation={props.navigation} />


  )





  ;
};

CategoryProductsScreen.navigationOptions = navigationData => {
  const catId = navigationData.navigation.getParam('categoryId');
  const catName = navigationData.navigation.getParam('categoryTitle')




  const selectedCategory = CATEGORIES.find(cat => cat.id === catId);

  return {
    headerTitle: catName,
  };
};

export default CategoryProductsScreen;
