import React from 'react';
import { ScrollView, View, Image, Text, StyleSheet, Button } from 'react-native';

import DefaultText from '../components/DefaultText';
import {PRODUCTS, Product} from '../data/dummy-data';
import { HeaderButtons, Item } from 'react-navigation-header-buttons';

import HeaderButton from '../components/HeaderButton';
import ProductsNavigator from '../navigation/ProductsNavigator';

const ProductDetailScreen = props => {

  const itemId = props.navigation.getParam('itemId');



    const selectedProduct = PRODUCTS.find(Product => Product.id === itemId)



  return (
    <ScrollView>
      <ProductsNavigator />

      <View style={styles.details}>
     <DefaultText>{selectedProduct.id}</DefaultText>
       <DefaultText>{selectedProduct.title.toUpperCase()}</DefaultText>
       <DefaultText>{selectedProduct.brand}</DefaultText>

      </View>

    </ScrollView>
  );
};

ProductDetailScreen.navigationOptions = navigationData => {

  const itemTitle = navigationData.navigation.getParam('itemTitle');
  const itemId = navigationData.navigation.getParam('itemId');



  const selectedProduct = PRODUCTS.find(Product => Product.id === itemId)


  return {
    headerTitle: itemTitle,



  };
};


const styles = StyleSheet.create({
  image: {
    width: '100%',
    height: 200
  },
  details: {
    flexDirection: 'row',
    padding: 15,
    paddingTop: 100,
    justifyContent: 'space-around'
  },
  title: {
    fontFamily: 'open-sans-bold',
    fontSize: 22,
    textAlign: 'center'
  },
  listItem: {
    marginVertical: 10,
    marginHorizontal: 20,
    borderColor: '#ccc',
    borderWidth: 1,
    padding: 10
  }
});

export default ProductDetailScreen;
