import React, { Component, useState, useEffect } from 'react';

import { ActivityIndicator, Button, Image, Alert, FlatList, Text, StyleSheet, View, TextInput, InteractionManager } from 'react-native';
import * as firebase from 'firebase';




const ProductDetailScreen = props => {

  const [entities, setEntities] = useState([])
  const sortedData = global.myData.filter(
    products => products.id === global.itemId);
  const [entityText, setEntityText] = useState('')

  const[favorites, setFavorites] = useState([]);
  const[itemIsFavorite, setItemIsFavorite] = useState(false);
  const [filteredArray, setFilteredArray] = useState([]);

  const entityRef = firebase.firestore().collection('Favorites')




    const getFavorites = async () =>{
      await
    entityRef
        .where("authorID", "==", global.myUserString)
        .onSnapshot(
            querySnapshot => {
                const newEntities = []
                querySnapshot.forEach(doc => {
                    const entity = doc.data()
                    entity.id = doc.id
                    newEntities.push(entity)

                });
                setEntities(newEntities)
                setFavorites(newEntities)





            },
            error => {
                console.log(error)
            }

        )
} 


const getFiltered = async () =>{
  await
  setItemIsFavorite(true)
  getFavorites();
   const filterArray = favorites.filter(data => data.title === global.itemTitle)
   setFilteredArray(filterArray);
  if (!filterArray.length){
    console.log("Array is empty")


    setItemIsFavorite(false)
    } else {
      console.log("filter has elements")


      setItemIsFavorite(true)
    }

};


useEffect(() => {
  getFiltered();

}, [filterArray]);


  const onAddButtonPress = () => {
    setItemIsFavorite(true)
    const timestamp = firebase.firestore.FieldValue.serverTimestamp();
    const data = {
      title: global.itemTitle,
      description: global.itemDescription,
      brand: global.itemBrand,
      category: global.itemCategory,
      stores: global.itemStore,
      authorID: global.myUserString,
      createdAt: timestamp,
      photo: global.itemPhoto
    };
    entityRef
      .add(data)
      .then(_doc => {
        setEntityText('')
        Keyboard.dismiss()
      })
      .catch((error) => {
        alert(error)
      });
  }


  return (

    <View style={styles.MainContainer}>


      <FlatList
        data={sortedData}
        keyExtractor={(item, index) => index.toString()}
        renderItem={({ item }) =>
          <View>
            <Text style={styles.row}>{item.title}</Text>
            <Text style={styles.row}>{item.description}</Text>

            <View>
              <Image
                style={styles.tinyLogo}
                source={{
                  uri: item.photo,
                }}
              />
            </View>
          </View>
        }
        style={{ marginTop: 10 }} />



        {itemIsFavorite === true? (
      <Text> This item is already a favorite of yours!</Text>
        ): (
          <Button title="Add to Favorites" onPress={onAddButtonPress} />
        )}

    </View>
  );
}




ProductDetailScreen.navigationOptions = navigationData => {

  global.itemTitle = navigationData.navigation.getParam('itemTitle');
  global.itemId = navigationData.navigation.getParam('itemId');
  global.itemDescription = navigationData.navigation.getParam("itemDescription");
  global.itemPhoto = navigationData.navigation.getParam("itemPhoto");
  global.itemStore = navigationData.navigation.getParam("itemStore");
  global.itemBrand = navigationData.navigation.getParam("itemBrand");
  global.itemCategory = navigationData.navigation.getParam("itemCategory");




  return {
    headerTitle: itemTitle,



  };
};


const styles = StyleSheet.create({

  MainContainer: {
    justifyContent: 'center',
    flex: 1,
    margin: 5,

  },
  tinyLogo: {
    width: 50,
    height: 50,
  },

  row: {
    fontSize: 18,
    padding: 12
  },

  textInput: {

    textAlign: 'center',
    height: 42,
    borderWidth: 1,
    borderColor: '#009688',
    borderRadius: 8,
    backgroundColor: "#FFFF"

  }
});

export default ProductDetailScreen;
